    console.log(evento.target.value)
  }

  render() {
    return (
      <form className="form-cadastro">
        <input
          type="text"
          placeholder="TÃ­tulo"
          className="form-cadastro_input"
          onChange={this.handleMudancaTitulo}
        />
        <textarea
          rows={15}
          placeholder="Escreva a sua nota..."
          className="form-cadastro_input"
